import React, { useState, useEffect } from 'react';  
import axios from 'axios';  
  
const TaskList = () => {  
  const [tasks, setTasks] = useState([]);  
  const [searchQuery, setSearchQuery] = useState('');  
  
  useEffect(() => {  
   axios.get('/api/tasks')  
    .then(response => {  
      setTasks(response.data);  
    })  
    .catch(error => {  
      console.error(error);  
    });  
  }, []);  
  
  const handleSearch = (event) => {  
   setSearchQuery(event.target.value);  
  };  
  
  const filteredTasks = tasks.filter(task => task.assignedTo.toLowerCase().includes(searchQuery.toLowerCase()) ||  
   task.status.toLowerCase().includes(searchQuery.toLowerCase()) ||  
   task.dueDate.toLowerCase().includes(searchQuery.toLowerCase()) ||  
   task.priority.toLowerCase().includes(searchQuery.toLowerCase()) ||  
   task.comments.toLowerCase().includes(searchQuery.toLowerCase()));  
  
  return (  
   <div>  
    <input  
      type="search"  
      value={searchQuery}  
      onChange={handleSearch}  
      placeholder="Search tasks"  
    />  
    <button onClick={() => setSearchQuery('')}>Clear Search</button>  
    <table>  
      <thead>  
       <tr>  
        <th>Assigned To</th>  
        <th>Status</th>  
        <th>Due Date</th>  
        <th>Priority</th>  
        <th>Comments</th>  
        <th>Actions</th>  
       </tr>  
      </thead>  
      <tbody>  
       {filteredTasks.map(task => (  
        <tr key={task.id}>  
          <td>{task.assignedTo}</td>  
          <td>{task.status}</td>  
          <td>{task.dueDate}</td>  
          <td>{task.priority}</td>  
          <td>{task.comments}</td>  
          <td>  
           <button>Edit</button>  
          </td>  
        </tr>  
       ))}  
      </tbody>  
    </table>  
   </div>  
  );  
};  
  
export default TaskList;
