import React, { useState } from 'react';


class TaskForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      task: props.currentTask ? props.currentTask.text : '',
    };
  }

  handleSubmit = (e) => {
    e.preventDefault();
    if (this.props.currentTask) {
      this.props.updateTask({ ...this.props.currentTask, text: this.state.task });
    } else {
      this.props.addTask(this.state.task);
    }
    this.setState({ task: '' });
  };

  handleChange = (e) => {
    this.setState({ task: e.target.value });
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <input
          type="text"
          value={this.state.task}
          onChange={this.handleChange}
          placeholder="Enter a task"
          required
        />
        <button type="submit">
          {this.props.currentTask ? 'Update Task' : 'Add Task'}
        </button>
      </form>
    );
  }
}

export default TaskForm;
