// src/components/Modal.js
import React from 'react';
import './TaskForm.css';

const TaskForm = ({ isOpen, onClose, onSubmit }) => {
  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    onSubmit(); // Call onSubmit to handle the submission
    onClose(); // Close the modal after submission
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>X</button>
        <h2>Add New Task</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" placeholder="Assigned To" required />
          <input type="text" placeholder="Status" required />
          <input type="date" placeholder="Due Date" required />
          <input type="text" placeholder="Priority" required />
          <textarea placeholder="Comments" required></textarea>
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default TaskForm;
