import React, { useState, useEffect } from 'react';  
import axios from 'axios';  
  
function TaskManagement() {  
  const [tasks, setTasks] = useState([]);  
  const [newTask, setNewTask] = useState('');  
  const [assignedTo, setAssignedTo] = useState('');  
  const [status, setStatus] = useState('');  
  const [dueDate, setDueDate] = useState('');  
  const [priority, setPriority] = useState('');  
  const [comments, setComments] = useState('');  
  const [currentPage, setCurrentPage] = useState(1);  
  const [tasksPerPage] = useState(5);  
  
  useEffect(() => {  
   axios.get('/api/tasks')  
    .then(response => {  
      setTasks(response.data);  
    })  
    .catch(error => {  
      console.error(error);  
    });  
  }, []);  
  
  const handleSubmit = (event) => {  
   event.preventDefault();  
   const task = {  
    assignedTo,  
    status,  
    dueDate,  
    priority,  
    comments  
   };  
     
  };  
  
  const handleDelete = (taskId) => {  
   axios.delete(`/api/tasks/${taskId}`)  
    .then(() => {  
      setTasks(tasks.filter(task => task._id !== taskId));  
    })  
    .catch(error => {  
      console.error(error);  
    });  
  };  
  
  const handleEdit = (taskId, updatedTask) => {  
   axios.put(`/api/tasks/${taskId}`, updatedTask)  
    .then(response => {  
      setTasks(tasks.map(task => task._id === taskId ? response.data : task));  
    })  
    .catch(error => {  
      console.error(error);  
    });  
  };  
  
  const handlePageChange = (pageNumber) => {  
   setCurrentPage(pageNumber);  
  };  
  
  const indexOfLastTask = currentPage * tasksPerPage;  
  const indexOfFirstTask = indexOfLastTask - tasksPerPage;  
  const currentTasks = tasks.slice(indexOfFirstTask, indexOfLastTask);  
  
  return (  
   <div>  
    <h1>Tasks</h1>  
    <form onSubmit={handleSubmit}>  
      <label>  
       Assigned To:  
       <input type="text" value={assignedTo} onChange={(event) => setAssignedTo(event.target.value)} />  
      </label>  
      <label>  
       Status:  
       <select value={status} onChange={(event) => setStatus(event.target.value)}>  
        <option value="">Select Status</option>  
        <option value="Not Started">Not Started</option>  
        <option value="In Progress">In Progress</option>  
        <option value="Completed">Completed</option>  
       </select>  
      </label>  
      <label>  
       Due Date:  
       <input type="date" value={dueDate} onChange={(event) => setDueDate(event.target.value)} />  
      </label>  
      <label>  
       Priority:  
       <select value={priority} onChange={(event) => setPriority(event.target.value)}>  
        <option value="">Select Priority</option>  
        <option value="Low">Low</option>  
        <option value="Normal">Normal</option>  
        <option value="High">High</option>  
       </select>  
      </label>  
      <label>  
       Comments:  
       <textarea value={comments} onChange={(event) => setComments(event.target.value)} />  
      </label>  
      <button type="submit">Create Task</button>  
    </form>  
    <table>  
      <thead>  
       <tr>  
        <th>Assigned To</th>  
        <th>Status</th>  
        <th>Due Date</th>  
        <th>Priority</th>  
        <th>Comments</th>  
        <th>Actions</th>  
       </tr>  
      </thead>  
      <tbody>  
       {currentTasks.map((task) => (  
        <tr key={task._id}>  
          <td>{task.assignedTo}</td>  
          <td>{task.status}</td>  
          <td>{task.dueDate}</td>  
          <td>{task.priority}</td>  
          <td>{task.comments}</td>  
          <td>  
           <button onClick={() => handleEdit(task._id, { ...task, status: 'Completed' })}>Complete</button>  
           <button onClick={() => handleDelete(task._id)}>Delete</button>  
          </td>  
        </tr>  
       ))}  
      </tbody>  
    </table>  
    <div>  
      <button onClick={() => handlePageChange(currentPage - 1)}>Previous</button>  
      <button onClick={() => handlePageChange(currentPage + 1)}>Next</button>  
    </div>  
   </div>  
  );  
}  
  
export default TaskManagement;
