import React, { useState, useEffect } from 'react';  
import axios from 'axios';  
  
function TaskManagement() {  
  const [tasks, setTasks] = useState([]);  
  const [newTask, setNewTask] = useState({  
   assignedTo: '',  
   status: '',  
   dueDate: '',  
   priority: '',  
   comments: ''  
  });  
  const [searchQuery, setSearchQuery] = useState('');  
  
  useEffect(() => {  
   axios.get('/api/tasks')  
    .then(response => {  
      setTasks(response.data);  
    })  
    .catch(error => {  
      console.error(error);  
    });  
  }, []);  
  
  const handleSearch = (event) => {  
   setSearchQuery(event.target.value);  
  };  
  
  const handleNewTask = (event) => {  
   setNewTask({ ...newTask, [event.target.name]: event.target.value });  
  };  
  
  const handleSubmitNewTask = (event) => {  
   event.preventDefault();  
   axios.post('/api/tasks', newTask)  
    .then(response => {  
      setTasks([...tasks, response.data]);  
      setNewTask({  
       assignedTo: '',  
       status: '',  
       dueDate: '',  
       priority: '',  
       comments: ''  
      });  
    })  
    .catch(error => {  
      console.error(error);  
    });  
  };  
  
  const handleEditTask = (task) => {  
   // Implement edit task logic here  
  };  
  
  return (  
   <div>  
    <h1>Tasks</h1>  
    <input  
      type="search"  
      value={searchQuery}  
      onChange={handleSearch}  
      placeholder="Search tasks"  
    />  
    <button onClick={() => setSearchQuery('')}>Clear Search</button>  
    <table>  
      <thead>  
       <tr>  
        <th>Assigned To</th>  
        <th>Status</th>  
        <th>Due Date</th>  
        <th>Priority</th>  
        <th>Comments</th>  
        <th>Actions</th>  
       </tr>  
      </thead>  
      <tbody>  
       {tasks.filter(task => task.assignedTo.toLowerCase().includes(searchQuery.toLowerCase()) ||  
        task.status.toLowerCase().includes(searchQuery.toLowerCase()) ||  
        task.dueDate.toLowerCase().includes(searchQuery.toLowerCase()) ||  
        task.priority.toLowerCase().includes(searchQuery.toLowerCase()) ||  
        task.comments.toLowerCase().includes(searchQuery.toLowerCase()))  
        .map(task => (  
          <tr key={task.id}>  
           <td>{task.assignedTo}</td>  
           <td>{task.status}</td>  
           <td>{task.dueDate}</td>  
           <td>{task.priority}</td>  
           <td>{task.comments}</td>  
           <td>  
            <button onClick={() => handleEditTask(task)}>Edit</button>  
           </td>  
          </tr>  
        ))}  
      </tbody>  
    </table>  
    <form onSubmit={handleSubmitNewTask}>  
      <label>  
       Assigned To:  
       <input  
        type="text"  
        name="assignedTo"  
        value={newTask.assignedTo}  
        onChange={handleNewTask}  
       />  
      </label>  
      <label>  
       Status:  
       <select  
        name="status"  
        value={newTask.status}  
        onChange={handleNewTask}  
       >  
        <option value="">Select Status</option>  
        <option value="Not Started">Not Started</option>  
        <option value="In Progress">In Progress</option>  
        <option value="Completed">Completed</option>  
       </select>  
      </label>  
      <label>  
       Due Date:  
       <input  
        type="date"  
        name="dueDate"  
        value={newTask.dueDate}  
        onChange={handleNewTask}  
       />  
      </label>  
      <label>  
       Priority:  
       <select  
        name="priority"  
        value={newTask.priority}  
        onChange={handleNewTask}  
       >  
        <option value="">Select Priority</option>  
        <option value="Low">Low</option>  
        <option value="Medium">Medium</option>  
        <option value="High">High</option>  
       </select>  
      </label>  
      <label>  
       Comments:  
       <textarea  
        name="comments"  
        value={newTask.comments}  
        onChange={handleNewTask}  
       />  
      </label>  
      <button type="submit">Create New Task</button>  
    </form>  
   </div>  
  );  
}  
  
export default TaskManagement;
